import { useNavigate } from "react-router-dom"
import React from "react";
import axios from "axios";



export default function Login(args){
    const [users, changeUsers] = React.useState([])
    const [login,     changeLogin    ] = React.useState('')
    const [password,  changePassword ] = React.useState('')
    const [error, changeError] = React.useState('')
    const navigete = useNavigate()


    function submitUser(){         
        let csrf = ''
        let token = ''
        if (document.cookie){
            csrf = document.cookie.split('; ')
            for(let i in csrf){
                if(csrf[i].includes('csrftoken')){
                    token = csrf[i].replace("csrftoken=",'')
                    break
                }
            }
        }
        if(password && login){
            axios.post('/api/login', {
                'username': login,
                'password': password,
                'X-CSRFToken': csrf,
              })
              .then(function (response) {
                console.log(response)
                axios.get('/api/is_authenticated')
                .then(request => request.data[0])
                .then(request => args.changeUser(request))
                navigete('/')
              })
              .catch(function (error) {
                console.log(error);
              });
            changeUsers(users)
            changeError('')
            changeLogin('')
            changePassword('')
        }
        else{
            changeError('Incorrect form filling')
        }
    }
    return(
        <>
        {!args.user  ? 
            <div className="registrationDiv">
                <p>Login</p>
                <span>Sign in</span>
                <div className="forms">
                    <input onChange={event => changeLogin(event.target.value)}     value ={login}     type="text"      placeholder="Login" />
                    <input onChange={event => changePassword(event.target.value)}  value ={password}  type="password"  placeholder="Password" />
                </div> 
                <p id="errortext">{error}</p>
                {/* <button type="submit" id="but" onClick={event => console.log( event.target.id)}>Create</button> */}
                <button onClick={submitUser} type="submit" id="but" >Sign in</button>
            </div>      
            :
            <div className="registrationDiv">
                <p>You are logined</p>
                <span>VasyAAA!!!</span>
            </div>      
        }

        </>
    )
}
