    console.log(description);
    console.log(category);
    const requestOptions = {
        method:'GET',
        headers:{
            'Content-Type': 'application/json',
            'Accept': 'application/json',
        },
        body:JSON.stringify({
            user:user,
            title:title,
            description:description,
            category:category,
