import React,{ useState } from 'react'
import './static/css/App.css';
import Header from './layout/header';
import Footer from './layout/footer';
import Registration from './pages/registration';
import Login from './pages/login';
import Cookie from './pages/coocie';
import Page404 from './pages/404';
import Home from './pages/home';
import CreateTodo from './pages/create_todo';
import { Routes, Route } from 'react-router-dom'

function App() {


  const headers = [
    {id:1,link:'',title:'Главная',component:<Home/>},
    {id:2,link:'login',title:'Логин',component:<Login/>},
    {id:3,link:'registration',title:'Регисстрация',component:<Registration/>},
    {id:4,link:'create_todo',title:'Создать заметку',component:<CreateTodo/>},
    {id:5,link:'api/create_todo',title:'Create todo',component:null},
    {id:6,link:'all',title:'Все заметки',component:null},
  ]
  const [title,changeTitle] = useState('Register')

  function change(){
    if (title === 'Register') {
      changeTitle('Login')      
    }else{
      changeTitle('Register')      
    }
  }


  return (
    <>
    <Cookie/>
    <Header headers={headers}/>
    {/* <h2 onClick={(() => change('title'))}>{title}</h2> */}
    <h2 onClick={change}>{title}</h2>

    <div className="content">
      <Routes>
        {/* {headers.map(x => {return(
          <Route key={x.id} path={x.link} element={x.component}/>
        )})} */}
        <Route path='' element={<Home/>}/>
        <Route path='/login' element={<Login/>}/>
        <Route path='/create_todo' element={<CreateTodo/>}/>
        <Route path='/registration' element={<Registration/>}/>
        <Route path='*' element={<Page404/>}/>
      </Routes>



    </div>
    <Footer/>
    </>
  );
}

export default App;
