import React from "react"


function CreateTodoFunc(user,title,description,category) {
    if (document.cookie){
        let cookie = document.cookie.split('; ')
        console.log(cookie)
    }
    console.log(user);
    console.log(title);
    console.log(description);
    console.log(category);
    const requestOptions = {
        method:'POST',
        
        headers:{
            'Content-Type': 'application/json',
            'Accept': 'application/json',
        },
        // 'X-CSRFToken': csrftoken,
        body:JSON.stringify({
            user:user,
            title:title,
            description:description,
            category:category,
        })
    }
    fetch('/api/',requestOptions)
    .then((response) => response.json())
    .then((data) => console.log(data))
}

export default class CreateTodo extends React.Component{
    state = {
        user:'',
        title:'',
        description:'',
        category:'',
    }    

    render(){
    return(
        <>
        <div className="registrationDiv">
            <p>Create Todo</p>
            <span>Remember everything</span>
            <form className="forms">
                
                <input onChange={event => this.setState({user:event.target.value})}     value ={this.state.user}     type="text"      placeholder="User" />
                <input onChange={event => this.setState({title:event.target.value})}     value ={this.state.title}     type="text"      placeholder="Title" />
                <input onChange={event => this.setState({description:event.target.value})}  value ={this.state.description}  type="text"  placeholder="Description" />
                <input onChange={event => this.setState({category:event.target.value})}  value ={this.state.category}  type="text"  placeholder="Category" />
            </form> 
            <p id="errortext"></p>
            {/* <button type="submit" id="but" onClick={event => console.log( event.target.id)}>Create</button> */}
            <button onClick={() => CreateTodoFunc(this.state.user,this.state.title,this.state.description,this.state.category)} type="submit" id="but" >Sign in</button>
        </div>          
        </>
    )
}}
