export default function TodoList(){
    const [todos, useTodos] = React.useState('')
    let myList = []
    useEffect(() => {
        fetch('http://127.0.0.1:8000/todo_list?format=json')
        .then(response => response.json())
        .then(data => myList.join(data))
    })
    return(
        <>
        <div className="registrationDiv">
            <p>Todo List</p>
            <p>{myList}</p>
        </div>          
        </>
    )
}
